import React, { useState, useEffect } from 'react';

const Fechamentos = () => {
  const [fechamentos, setFechamentos] = useState([]);
  const [pacientes, setPacientes] = useState([]);
  const [consultores, setConsultores] = useState([]);
  const [clinicas, setClinicas] = useState([]);
  const [agendamentos, setAgendamentos] = useState([]);
  const [erro, setErro] = useState(null);
  const [carregando, setCarregando] = useState(true);
  const [filtroConsultor, setFiltroConsultor] = useState('');
  const [filtroClinica, setFiltroClinica] = useState('');
  const [filtroMes, setFiltroMes] = useState('');
  const [filtrosVisiveis, setFiltrosVisiveis] = useState(false);
  const [modalAberto, setModalAberto] = useState(false);
  const [fechamentoEditando, setFechamentoEditando] = useState(null);
  const [novoFechamento, setNovoFechamento] = useState({
    paciente_id: '',
    consultor_id: '',
    clinica_id: '',
    agendamento_id: '',
    valor_fechado: '',
    data_fechamento: new Date().toISOString().split('T')[0],
    tipo_tratamento: '',
    forma_pagamento: '',
    observacoes: ''
  });

  const formasPagamento = [
    'À vista',
    'Parcelado 2x',
    'Parcelado 3x',
    'Parcelado 4x',
    'Parcelado 6x',
    'Parcelado 12x',
    'PIX',
    'Boleto',
    'Cartão de Crédito',
    'Cartão de Débito'
  ];

  useEffect(() => {
    carregarDados();
  }, []);

  const carregarDados = async () => {
    try {
      setCarregando(true);
      setErro(null);
      
      const [fechamentosRes, pacientesRes, consultoresRes, clinicasRes, agendamentosRes] = await Promise.all([
        fetch('http://localhost:5000/api/fechamentos'),
        fetch('http://localhost:5000/api/pacientes'),
        fetch('http://localhost:5000/api/consultores'),
        fetch('http://localhost:5000/api/clinicas'),
        fetch('http://localhost:5000/api/agendamentos')
      ]);

      // Verificar se todas as respostas foram bem-sucedidas
      if (!fechamentosRes.ok) {
        throw new Error(`Erro ao carregar fechamentos: ${fechamentosRes.status} - ${fechamentosRes.statusText}`);
      }

      const fechamentosData = await fechamentosRes.json();
      const pacientesData = await pacientesRes.json();
      const consultoresData = await consultoresRes.json();
      const clinicasData = await clinicasRes.json();
      const agendamentosData = await agendamentosRes.json();

      // Garantir que sempre sejam arrays
      setFechamentos(Array.isArray(fechamentosData) ? fechamentosData : []);
      setPacientes(Array.isArray(pacientesData) ? pacientesData : []);
      setConsultores(Array.isArray(consultoresData) ? consultoresData : []);
      setClinicas(Array.isArray(clinicasData) ? clinicasData : []);
      setAgendamentos(Array.isArray(agendamentosData) ? agendamentosData : []);
      
      setCarregando(false);
    } catch (error) {
      console.error('Erro ao carregar dados:', error);
      setErro(error.message);
      setCarregando(false);
      
      // Em caso de erro, garantir arrays vazios
      setFechamentos([]);
      setPacientes([]);
      setConsultores([]);
      setClinicas([]);
      setAgendamentos([]);
    }
  };

  const filtrarFechamentos = () => {
    if (!Array.isArray(fechamentos)) {
      return [];
    }
    return fechamentos.filter(fechamento => {
      const consultorMatch = !filtroConsultor || fechamento.consultor_id === parseInt(filtroConsultor);
      const clinicaMatch = !filtroClinica || fechamento.clinica_id === parseInt(filtroClinica);
      
      let mesMatch = true;
      if (filtroMes) {
        const dataFechamento = new Date(fechamento.data_fechamento);
        const [ano, mes] = filtroMes.split('-');
        mesMatch = dataFechamento.getFullYear() === parseInt(ano) && 
                   dataFechamento.getMonth() === parseInt(mes) - 1;
      }

      return consultorMatch && clinicaMatch && mesMatch;
    });
  };

  const calcularEstatisticas = () => {
    const fechamentosFiltrados = filtrarFechamentos();
    const total = fechamentosFiltrados.length;
    const valorTotal = fechamentosFiltrados.reduce((acc, f) => acc + parseFloat(f.valor_fechado || 0), 0);
    const ticketMedio = total > 0 ? valorTotal / total : 0;
    
    const hoje = new Date().toISOString().split('T')[0];
    const fechamentosHoje = fechamentosFiltrados.filter(f => f.data_fechamento === hoje).length;

    const mesAtual = new Date();
    const fechamentosMes = fechamentosFiltrados.filter(f => {
      const dataFechamento = new Date(f.data_fechamento);
      return dataFechamento.getMonth() === mesAtual.getMonth() && 
             dataFechamento.getFullYear() === mesAtual.getFullYear();
    }).length;

    return { total, valorTotal, ticketMedio, fechamentosHoje, fechamentosMes };
  };

  const contarFiltrosAtivos = () => {
    let count = 0;
    if (filtroConsultor) count++;
    if (filtroClinica) count++;
    if (filtroMes) count++;
    return count;
  };

  const limparFiltros = () => {
    setFiltroConsultor('');
    setFiltroClinica('');
    setFiltroMes('');
  };

  const abrirModal = (fechamento = null) => {
    if (fechamento) {
      setFechamentoEditando(fechamento);
      setNovoFechamento({ ...fechamento });
    } else {
      setFechamentoEditando(null);
      setNovoFechamento({
        paciente_id: '',
        consultor_id: '',
        clinica_id: '',
        agendamento_id: '',
        valor_fechado: '',
        data_fechamento: new Date().toISOString().split('T')[0],
        tipo_tratamento: '',
        forma_pagamento: '',
        observacoes: ''
      });
    }
    setModalAberto(true);
  };

  const fecharModal = () => {
    setModalAberto(false);
    setFechamentoEditando(null);
  };

  const salvarFechamento = async () => {
    try {
      const url = fechamentoEditando 
        ? `http://localhost:5000/api/fechamentos/${fechamentoEditando.id}`
        : 'http://localhost:5000/api/fechamentos';
      
      const response = await fetch(url, {
        method: fechamentoEditando ? 'PUT' : 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(novoFechamento)
      });

      if (response.ok) {
        carregarDados();
        fecharModal();
        alert(fechamentoEditando ? 'Fechamento atualizado!' : 'Fechamento registrado!');
      }
    } catch (error) {
      console.error('Erro ao salvar fechamento:', error);
    }
  };

  const excluirFechamento = async (id) => {
    if (window.confirm('Deseja excluir este fechamento?')) {
      try {
        const response = await fetch(`http://localhost:5000/api/fechamentos/${id}`, {
          method: 'DELETE'
        });

        if (response.ok) {
          carregarDados();
          alert('Fechamento excluído!');
        }
      } catch (error) {
        console.error('Erro ao excluir fechamento:', error);
      }
    }
  };

  const formatarMoeda = (valor) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(valor);
  };

  const formatarData = (data) => {
    return new Date(data + 'T00:00:00').toLocaleDateString('pt-BR');
  };

  const fechamentosFiltrados = filtrarFechamentos();
  const stats = calcularEstatisticas();
  const filtrosAtivos = contarFiltrosAtivos();

  return (
    <div className="fechamentos-container">
      <style>{`
        .fechamentos-container {
          padding: 20px;
          max-width: 1400px;
          margin: 0 auto;
        }

        .stats-section {
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          border-radius: 15px;
          padding: 30px;
          margin-bottom: 30px;
          box-shadow: 0 8px 32px rgba(0,0,0,0.1);
          color: white;
        }

        .stats-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 25px;
        }

        .stats-title {
          font-size: 28px;
          font-weight: 600;
          margin: 0;
        }

        .btn-novo {
          background: rgba(255,255,255,0.2);
          color: white;
          border: 1px solid rgba(255,255,255,0.3);
          padding: 12px 24px;
          border-radius: 8px;
          cursor: pointer;
          font-weight: 500;
          transition: all 0.3s ease;
        }

        .btn-novo:hover {
          background: rgba(255,255,255,0.3);
          transform: translateY(-2px);
        }

        .stats-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
          gap: 20px;
          margin-bottom: 20px;
        }

        .stat-card {
          background: rgba(255,255,255,0.15);
          border-radius: 12px;
          padding: 20px;
          text-align: center;
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255,255,255,0.2);
        }

        .stat-number {
          font-size: 32px;
          font-weight: 700;
          margin-bottom: 5px;
          display: block;
        }

        .stat-label {
          font-size: 14px;
          opacity: 0.9;
          font-weight: 500;
        }

        .alert-hoje {
          background: rgba(255, 193, 7, 0.2);
          border: 1px solid rgba(255, 193, 7, 0.4);
          border-radius: 8px;
          padding: 15px;
          text-align: center;
          color: #fff3cd;
          font-weight: 500;
        }

        .filtros-section {
          background: white;
          border-radius: 15px;
          padding: 25px;
          margin-bottom: 25px;
          box-shadow: 0 4px 20px rgba(0,0,0,0.08);
          border: 1px solid #e5e7eb;
        }

        .filtros-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 20px;
        }

        .filtros-toggle {
          background: #3b82f6;
          color: white;
          border: none;
          padding: 10px 20px;
          border-radius: 8px;
          cursor: pointer;
          display: flex;
          align-items: center;
          gap: 8px;
          font-weight: 500;
          transition: all 0.3s ease;
        }

        .filtros-toggle:hover {
          background: #2563eb;
        }

        .filtros-badge {
          background: #dc2626;
          color: white;
          border-radius: 50%;
          width: 20px;
          height: 20px;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 12px;
          font-weight: 600;
        }

        .filtros-content {
          display: ${filtrosVisiveis ? 'block' : 'none'};
          animation: ${filtrosVisiveis ? 'slideDown 0.3s ease' : ''};
        }

        .filtros-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
          gap: 15px;
          margin-bottom: 20px;
        }

        .form-group {
          display: flex;
          flex-direction: column;
        }

        .form-group label {
          font-weight: 500;
          margin-bottom: 5px;
          color: #374151;
          display: flex;
          align-items: center;
          gap: 5px;
        }

        .form-group select, .form-group input {
          padding: 10px;
          border: 1px solid #d1d5db;
          border-radius: 6px;
          font-size: 14px;
          transition: all 0.3s ease;
        }

        .form-group select:focus, .form-group input:focus {
          outline: none;
          border-color: #3b82f6;
          box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
        }

        .resultado-info {
          text-align: center;
          color: #6b7280;
          font-weight: 500;
          margin-bottom: 20px;
        }

        .btn-limpar {
          background: #6b7280;
          color: white;
          border: none;
          padding: 10px 20px;
          border-radius: 6px;
          cursor: pointer;
          transition: all 0.3s ease;
        }

        .btn-limpar:hover {
          background: #4b5563;
        }

        .fechamentos-grid {
          display: grid;
          gap: 20px;
        }

        .fechamento-card {
          background: white;
          border-radius: 12px;
          padding: 20px;
          box-shadow: 0 4px 20px rgba(0,0,0,0.08);
          border: 1px solid #e5e7eb;
          transition: all 0.3s ease;
        }

        .fechamento-card:hover {
          transform: translateY(-2px);
          box-shadow: 0 8px 30px rgba(0,0,0,0.12);
        }

        .fechamento-header {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          margin-bottom: 15px;
        }

        .fechamento-info h3 {
          margin: 0 0 5px 0;
          color: #1f2937;
          font-size: 18px;
          font-weight: 600;
        }

        .fechamento-valor {
          font-size: 24px;
          font-weight: 700;
          color: #059669;
          margin: 0;
        }

        .fechamento-data {
          background: #f3f4f6;
          color: #374151;
          padding: 4px 12px;
          border-radius: 20px;
          font-size: 12px;
          font-weight: 500;
        }

        .fechamento-details {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
          gap: 10px;
          margin-bottom: 15px;
        }

        .detail-item {
          padding: 8px 0;
          border-bottom: 1px solid #f3f4f6;
        }

        .detail-label {
          font-size: 12px;
          color: #6b7280;
          font-weight: 500;
          margin-bottom: 2px;
        }

        .detail-value {
          font-size: 14px;
          color: #1f2937;
          font-weight: 500;
        }

        .fechamento-actions {
          display: flex;
          gap: 10px;
          justify-content: flex-end;
        }

        .btn-editar, .btn-excluir {
          padding: 8px 16px;
          border: none;
          border-radius: 6px;
          cursor: pointer;
          font-size: 12px;
          font-weight: 500;
          transition: all 0.3s ease;
        }

        .btn-editar {
          background: #3b82f6;
          color: white;
        }

        .btn-editar:hover {
          background: #2563eb;
        }

        .btn-excluir {
          background: #dc2626;
          color: white;
        }

        .btn-excluir:hover {
          background: #b91c1c;
        }

        .modal {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background: rgba(0,0,0,0.5);
          display: ${modalAberto ? 'flex' : 'none'};
          align-items: center;
          justify-content: center;
          z-index: 1000;
        }

        .modal-content {
          background: white;
          border-radius: 15px;
          padding: 30px;
          width: 90%;
          max-width: 600px;
          max-height: 90vh;
          overflow-y: auto;
          box-shadow: 0 20px 60px rgba(0,0,0,0.3);
        }

        .modal-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 25px;
          padding-bottom: 15px;
          border-bottom: 1px solid #e5e7eb;
        }

        .modal-title {
          font-size: 24px;
          font-weight: 600;
          color: #1f2937;
          margin: 0;
        }

        .btn-fechar {
          background: none;
          border: none;
          font-size: 24px;
          cursor: pointer;
          color: #6b7280;
          width: 32px;
          height: 32px;
          display: flex;
          align-items: center;
          justify-content: center;
          border-radius: 6px;
          transition: all 0.3s ease;
        }

        .btn-fechar:hover {
          background: #f3f4f6;
          color: #374151;
        }

        .modal-form {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
          gap: 20px;
          margin-bottom: 25px;
        }

        .form-group-full {
          grid-column: 1 / -1;
        }

        .form-group textarea {
          padding: 10px;
          border: 1px solid #d1d5db;
          border-radius: 6px;
          font-size: 14px;
          min-height: 80px;
          resize: vertical;
          font-family: inherit;
        }

        .modal-actions {
          display: flex;
          gap: 10px;
          justify-content: flex-end;
          padding-top: 15px;
          border-top: 1px solid #e5e7eb;
        }

        .btn-salvar, .btn-cancelar {
          padding: 12px 24px;
          border: none;
          border-radius: 8px;
          cursor: pointer;
          font-weight: 500;
          transition: all 0.3s ease;
        }

        .btn-salvar {
          background: #059669;
          color: white;
        }

        .btn-salvar:hover {
          background: #047857;
        }

        .btn-cancelar {
          background: #6b7280;
          color: white;
        }

        .btn-cancelar:hover {
          background: #4b5563;
        }

        .empty-state {
          text-align: center;
          padding: 60px 20px;
          color: #6b7280;
        }

        .empty-icon {
          font-size: 64px;
          margin-bottom: 20px;
          opacity: 0.5;
        }

        @keyframes slideDown {
          from { opacity: 0; transform: translateY(-10px); }
          to { opacity: 1; transform: translateY(0); }
        }

        @media (max-width: 768px) {
          .fechamentos-container {
            padding: 15px;
          }
          
          .stats-grid {
            grid-template-columns: repeat(2, 1fr);
          }
          
          .filtros-grid {
            grid-template-columns: 1fr;
          }
          
          .modal-form {
            grid-template-columns: 1fr;
          }
        }
      `}</style>

      {/* Seção de Estatísticas */}
      <div className="stats-section">
        <div className="stats-header">
          <h1 className="stats-title">💰 Fechamentos</h1>
          <button className="btn-novo" onClick={() => abrirModal()}>
            ➕ Novo Fechamento
          </button>
        </div>

        <div className="stats-grid">
          <div className="stat-card">
            <span className="stat-number">{stats.total}</span>
            <div className="stat-label">Total de Fechamentos</div>
          </div>
          <div className="stat-card">
            <span className="stat-number">{formatarMoeda(stats.valorTotal)}</span>
            <div className="stat-label">Valor Total</div>
          </div>
          <div className="stat-card">
            <span className="stat-number">{formatarMoeda(stats.ticketMedio)}</span>
            <div className="stat-label">Ticket Médio</div>
          </div>
          <div className="stat-card">
            <span className="stat-number">{stats.fechamentosHoje}</span>
            <div className="stat-label">Fechamentos Hoje</div>
          </div>
          <div className="stat-card">
            <span className="stat-number">{stats.fechamentosMes}</span>
            <div className="stat-label">Fechamentos no Mês</div>
          </div>
        </div>

        {stats.fechamentosHoje > 0 && (
          <div className="alert-hoje">
            🎉 Parabéns! {stats.fechamentosHoje} fechamento(s) realizado(s) hoje!
          </div>
        )}
      </div>

      {/* Seção de Filtros */}
      <div className="filtros-section">
        <div className="filtros-header">
          <button 
            className="filtros-toggle"
            onClick={() => setFiltrosVisiveis(!filtrosVisiveis)}
          >
            🔍 Filtros {filtrosVisiveis ? '▲' : '▼'}
            {filtrosAtivos > 0 && (
              <div className="filtros-badge">{filtrosAtivos}</div>
            )}
          </button>
          <div className="resultado-info">
            {fechamentosFiltrados.length} resultado(s) encontrado(s)
          </div>
        </div>

        <div className="filtros-content">
          <div className="filtros-grid">
            <div className="form-group">
              <label>👨‍💼 Consultor</label>
              <select 
                value={filtroConsultor} 
                onChange={(e) => setFiltroConsultor(e.target.value)}
              >
                <option value="">Todos os consultores</option>
                {consultores.map(consultor => (
                  <option key={consultor.id} value={consultor.id}>
                    {consultor.nome}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>🏥 Clínica</label>
              <select 
                value={filtroClinica} 
                onChange={(e) => setFiltroClinica(e.target.value)}
              >
                <option value="">Todas as clínicas</option>
                {clinicas.map(clinica => (
                  <option key={clinica.id} value={clinica.id}>
                    {clinica.nome}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>📅 Mês/Ano</label>
              <input 
                type="month"
                value={filtroMes} 
                onChange={(e) => setFiltroMes(e.target.value)}
              />
            </div>
          </div>

          {filtrosAtivos > 0 && (
            <button className="btn-limpar" onClick={limparFiltros}>
              🗑️ Limpar Filtros
            </button>
          )}
        </div>
      </div>

      {/* Lista de Fechamentos */}
      <div className="fechamentos-grid">
        {fechamentosFiltrados.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">💰</div>
            <h3>Nenhum fechamento encontrado</h3>
            <p>Registre seu primeiro fechamento para começar!</p>
          </div>
        ) : (
          fechamentosFiltrados.map(fechamento => (
            <div key={fechamento.id} className="fechamento-card">
              <div className="fechamento-header">
                <div className="fechamento-info">
                  <h3>{fechamento.paciente_nome}</h3>
                  <div className="fechamento-valor">
                    {formatarMoeda(fechamento.valor_fechado)}
                  </div>
                </div>
                <div className="fechamento-data">
                  {formatarData(fechamento.data_fechamento)}
                </div>
              </div>

              <div className="fechamento-details">
                <div className="detail-item">
                  <div className="detail-label">Consultor</div>
                  <div className="detail-value">{fechamento.consultor_nome || 'Não informado'}</div>
                </div>
                <div className="detail-item">
                  <div className="detail-label">Clínica</div>
                  <div className="detail-value">{fechamento.clinica_nome || 'Não informado'}</div>
                </div>
                <div className="detail-item">
                  <div className="detail-label">Tipo de Tratamento</div>
                  <div className="detail-value">{fechamento.tipo_tratamento || 'Não informado'}</div>
                </div>
                <div className="detail-item">
                  <div className="detail-label">Forma de Pagamento</div>
                  <div className="detail-value">{fechamento.forma_pagamento || 'Não informado'}</div>
                </div>
              </div>

              {fechamento.observacoes && (
                <div className="detail-item">
                  <div className="detail-label">Observações</div>
                  <div className="detail-value">{fechamento.observacoes}</div>
                </div>
              )}

              <div className="fechamento-actions">
                <button 
                  className="btn-editar" 
                  onClick={() => abrirModal(fechamento)}
                >
                  ✏️ Editar
                </button>
                <button 
                  className="btn-excluir" 
                  onClick={() => excluirFechamento(fechamento.id)}
                >
                  🗑️ Excluir
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Modal de Cadastro/Edição */}
      <div className="modal">
        <div className="modal-content">
          <div className="modal-header">
            <h2 className="modal-title">
              {fechamentoEditando ? 'Editar Fechamento' : 'Novo Fechamento'}
            </h2>
            <button className="btn-fechar" onClick={fecharModal}>×</button>
          </div>

          <div className="modal-form">
            <div className="form-group">
              <label>👤 Paciente *</label>
              <select 
                value={novoFechamento.paciente_id} 
                onChange={(e) => setNovoFechamento({...novoFechamento, paciente_id: e.target.value})}
                required
              >
                <option value="">Selecione um paciente</option>
                {pacientes.map(paciente => (
                  <option key={paciente.id} value={paciente.id}>
                    {paciente.nome} - {paciente.telefone}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>👨‍💼 Consultor</label>
              <select 
                value={novoFechamento.consultor_id} 
                onChange={(e) => setNovoFechamento({...novoFechamento, consultor_id: e.target.value})}
              >
                <option value="">Selecione um consultor</option>
                {consultores.map(consultor => (
                  <option key={consultor.id} value={consultor.id}>
                    {consultor.nome}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>🏥 Clínica</label>
              <select 
                value={novoFechamento.clinica_id} 
                onChange={(e) => setNovoFechamento({...novoFechamento, clinica_id: e.target.value})}
              >
                <option value="">Selecione uma clínica</option>
                {clinicas.map(clinica => (
                  <option key={clinica.id} value={clinica.id}>
                    {clinica.nome}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>📅 Agendamento (opcional)</label>
              <select 
                value={novoFechamento.agendamento_id} 
                onChange={(e) => setNovoFechamento({...novoFechamento, agendamento_id: e.target.value})}
              >
                <option value="">Selecione um agendamento</option>
                {agendamentos
                  .filter(ag => ag.paciente_id == novoFechamento.paciente_id)
                  .map(agendamento => (
                    <option key={agendamento.id} value={agendamento.id}>
                      {formatarData(agendamento.data_agendamento)} - {agendamento.horario}
                    </option>
                  ))}
              </select>
            </div>

            <div className="form-group">
              <label>💰 Valor Fechado *</label>
              <input 
                type="number" 
                step="0.01"
                placeholder="0,00"
                value={novoFechamento.valor_fechado} 
                onChange={(e) => setNovoFechamento({...novoFechamento, valor_fechado: e.target.value})}
                required
              />
            </div>

            <div className="form-group">
              <label>📅 Data do Fechamento *</label>
              <input 
                type="date"
                value={novoFechamento.data_fechamento} 
                onChange={(e) => setNovoFechamento({...novoFechamento, data_fechamento: e.target.value})}
                required
              />
            </div>

            <div className="form-group">
              <label>🦷 Tipo de Tratamento</label>
              <input 
                type="text"
                placeholder="Ex: Implante, Ortodontia, Limpeza..."
                value={novoFechamento.tipo_tratamento} 
                onChange={(e) => setNovoFechamento({...novoFechamento, tipo_tratamento: e.target.value})}
              />
            </div>

            <div className="form-group">
              <label>💳 Forma de Pagamento</label>
              <select 
                value={novoFechamento.forma_pagamento} 
                onChange={(e) => setNovoFechamento({...novoFechamento, forma_pagamento: e.target.value})}
              >
                <option value="">Selecione a forma de pagamento</option>
                {formasPagamento.map(forma => (
                  <option key={forma} value={forma}>
                    {forma}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group form-group-full">
              <label>📝 Observações</label>
              <textarea 
                placeholder="Informações adicionais sobre o fechamento..."
                value={novoFechamento.observacoes} 
                onChange={(e) => setNovoFechamento({...novoFechamento, observacoes: e.target.value})}
              />
            </div>
          </div>

          <div className="modal-actions">
            <button className="btn-cancelar" onClick={fecharModal}>
              Cancelar
            </button>
            <button className="btn-salvar" onClick={salvarFechamento}>
              {fechamentoEditando ? 'Atualizar' : 'Salvar'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Fechamentos;
