      nao_compareceu: '#8b5cf6',
      reagendado: '#1e40af',
      nao_passou_cpf: '#4f46e5'
    };
    return colors[status] || '#64748b';
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
