import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Clinicas = () => {
  const [clinicas, setClinicas] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingClinica, setEditingClinica] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [filtroCity, setFiltroCity] = useState('');
  const [formData, setFormData] = useState({
    nome: '',
    endereco: '',
    bairro: '',
    cidade: '',
    telefone: '',
    email: ''
  });

  useEffect(() => {
    fetchClinicas();
  }, []);

  const fetchClinicas = async () => {
    try {
      const response = await axios.get('/api/clinicas');
      setClinicas(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Erro ao carregar clínicas:', error);
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingClinica) {
        await axios.put(`/api/clinicas/${editingClinica.id}`, formData);
        setMessage('Clínica atualizada com sucesso!');
      } else {
        await axios.post('/api/clinicas', formData);
        setMessage('Clínica cadastrada com sucesso!');
      }
      
      setShowModal(false);
      setEditingClinica(null);
      setFormData({
        nome: '',
        endereco: '',
        bairro: '',
        cidade: '',
        telefone: '',
        email: ''
      });
      fetchClinicas();
      
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      console.error('Erro ao salvar clínica:', error);
      setMessage('Erro ao salvar clínica');
    }
  };

  const handleEdit = (clinica) => {
    setEditingClinica(clinica);
    setFormData({
      nome: clinica.nome || '',
      endereco: clinica.endereco || '',
      bairro: clinica.bairro || '',
      cidade: clinica.cidade || '',
      telefone: clinica.telefone || '',
      email: clinica.email || ''
    });
    setShowModal(true);
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const formatarData = (data) => {
    return new Date(data).toLocaleDateString('pt-BR');
  };

  const formatarTelefone = (telefone) => {
    if (!telefone) return '';
    const numbers = telefone.replace(/\D/g, '');
    if (numbers.length === 11) {
      return `(${numbers.substring(0, 2)}) ${numbers.substring(2, 7)}-${numbers.substring(7)}`;
    }
    return telefone;
  };

  const resetForm = () => {
    setFormData({
      nome: '',
      endereco: '',
      bairro: '',
      cidade: '',
      telefone: '',
      email: ''
    });
    setEditingClinica(null);
    setShowModal(false);
  };

  // Filtrar clínicas por cidade
  const clinicasFiltradas = filtroCity 
    ? clinicas.filter(clinica => 
        clinica.cidade?.toLowerCase().includes(filtroCity.toLowerCase())
      )
    : clinicas;

  // Obter lista única de cidades para o filtro
  const cidadesDisponiveis = [...new Set(clinicas
    .map(c => c.cidade)
    .filter(cidade => cidade && cidade.trim() !== '')
  )].sort();

  return (
    <div>
      <div className="page-header">
        <h1 className="page-title">🏥 Gestão de Clínicas</h1>
        <p className="page-subtitle">Cadastre as clínicas parceiras do sistema</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('sucesso') ? 'alert-success' : 'alert-error'}`}>
          {message}
        </div>
      )}

      <div className="card">
        <div className="card-header">
          <h2 className="card-title">🏥 Lista de Clínicas</h2>
          <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
            {/* Filtro por Cidade */}
            <div style={{ display: 'flex', flexDirection: 'column', minWidth: '200px' }}>
              <label style={{ fontSize: '0.9rem', color: '#4a5568', marginBottom: '0.25rem' }}>
                Filtrar por Cidade:
              </label>
              <select
                value={filtroCity}
                onChange={(e) => setFiltroCity(e.target.value)}
                style={{
                  padding: '0.5rem',
                  borderRadius: '6px',
                  border: '2px solid #e2e8f0',
                  fontSize: '0.9rem'
                }}
              >
                <option value="">Todas as cidades</option>
                {cidadesDisponiveis.map(cidade => (
                  <option key={cidade} value={cidade}>{cidade}</option>
                ))}
              </select>
            </div>
            
            <button 
              className="btn btn-primary"
              onClick={() => setShowModal(true)}
            >
              ➕ Nova Clínica
            </button>
          </div>
        </div>

        {loading ? (
          <p>Carregando clínicas...</p>
        ) : clinicasFiltradas.length === 0 ? (
          <p style={{ textAlign: 'center', color: '#718096', padding: '2rem' }}>
            {filtroCity 
              ? `Nenhuma clínica encontrada na cidade "${filtroCity}".`
              : 'Nenhuma clínica cadastrada ainda. Clique em "Nova Clínica" para começar.'
            }
          </p>
        ) : (
          <>
            {filtroCity && (
              <div style={{ 
                background: '#e3f2fd', 
                padding: '0.75rem', 
                borderRadius: '6px', 
                marginBottom: '1rem',
                color: '#1565c0',
                fontSize: '0.9rem'
              }}>
                📍 Mostrando {clinicasFiltradas.length} clínica(s) em <strong>{filtroCity}</strong>
              </div>
            )}
            
            <div className="grid grid-2">
              {clinicasFiltradas.map(clinica => (
                <div key={clinica.id} className="card card-grid-item" style={{ margin: 0, position: 'relative' }}>
                  <div style={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    marginBottom: '1rem',
                    paddingBottom: '0.75rem',
                    borderBottom: '2px solid #667eea'
                  }}>
                    <span style={{ fontSize: '2rem', marginRight: '0.75rem' }}>🏥</span>
                    <h3 style={{ 
                      fontSize: '1.25rem', 
                      fontWeight: '600', 
                      color: '#2d3748',
                      margin: 0,
                      flex: 1
                    }}>
                      {clinica.nome}
                    </h3>
                    <button
                      onClick={() => handleEdit(clinica)}
                      className="btn-edit card-edit-btn"
                    >
                      ✏️
                    </button>
                  </div>
                  
                  <div style={{ display: 'grid', gap: '0.75rem' }}>
                    {clinica.endereco && (
                      <div style={{ display: 'flex', alignItems: 'flex-start' }}>
                        <span style={{ marginRight: '0.5rem' }}>📍</span>
                        <span style={{ color: '#4a5568' }}>{clinica.endereco}</span>
                      </div>
                    )}

                    {clinica.bairro && (
                      <div style={{ display: 'flex', alignItems: 'center' }}>
                        <span style={{ marginRight: '0.5rem' }}>🏘️</span>
                        <span style={{ color: '#4a5568' }}>{clinica.bairro}</span>
                      </div>
                    )}

                    {clinica.cidade && (
                      <div style={{ display: 'flex', alignItems: 'center' }}>
                        <span style={{ marginRight: '0.5rem' }}>🌆</span>
                        <span style={{ 
                          color: '#667eea', 
                          fontWeight: '600',
                          padding: '0.25rem 0.5rem',
                          background: '#e3f2fd',
                          borderRadius: '12px',
                          fontSize: '0.85rem'
                        }}>
                          {clinica.cidade}
                        </span>
                      </div>
                    )}
                    
                    {clinica.telefone && (
                      <div style={{ display: 'flex', alignItems: 'center' }}>
                        <span style={{ marginRight: '0.5rem' }}>📞</span>
                        <span style={{ color: '#4a5568' }}>{formatarTelefone(clinica.telefone)}</span>
                      </div>
                    )}
                    
                    {clinica.email && (
                      <div style={{ display: 'flex', alignItems: 'center' }}>
                        <span style={{ marginRight: '0.5rem' }}>📧</span>
                        <a 
                          href={`mailto:${clinica.email}`}
                          style={{ color: '#667eea', textDecoration: 'none' }}
                        >
                          {clinica.email}
                        </a>
                      </div>
                    )}
                    
                    <div style={{ 
                      marginTop: '1rem',
                      paddingTop: '0.75rem',
                      borderTop: '1px solid #e2e8f0',
                      fontSize: '0.85rem',
                      color: '#718096'
                    }}>
                      Cadastrada em: {formatarData(clinica.created_at)}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}
      </div>

      {/* Modal de Cadastro/Edição */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2 className="modal-title">
                {editingClinica ? '✏️ Editar Clínica' : '🏥 Nova Clínica'}
              </h2>
              <button 
                className="close-btn"
                onClick={resetForm}
              >
                ✕
              </button>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="form-label">Nome da Clínica *</label>
                <input
                  type="text"
                  name="nome"
                  className="form-input"
                  value={formData.nome}
                  onChange={handleInputChange}
                  placeholder="Digite o nome da clínica"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Endereço (Rua e Número)</label>
                <input
                  type="text"
                  name="endereco"
                  className="form-input"
                  value={formData.endereco}
                  onChange={handleInputChange}
                  placeholder="Ex: Rua das Flores, 123"
                />
              </div>

              <div className="grid grid-2">
                <div className="form-group">
                  <label className="form-label">Bairro/Zona</label>
                  <input
                    type="text"
                    name="bairro"
                    className="form-input"
                    value={formData.bairro}
                    onChange={handleInputChange}
                    placeholder="Ex: Centro, Zona Sul"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Cidade *</label>
                  <input
                    type="text"
                    name="cidade"
                    className="form-input"
                    value={formData.cidade}
                    onChange={handleInputChange}
                    placeholder="Ex: São Paulo, Rio de Janeiro"
                    required
                  />
                </div>
              </div>

              <div className="grid grid-2">
                <div className="form-group">
                  <label className="form-label">Telefone</label>
                  <input
                    type="tel"
                    name="telefone"
                    className="form-input"
                    value={formData.telefone}
                    onChange={handleInputChange}
                    placeholder="(11) 99999-9999"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    name="email"
                    className="form-input"
                    value={formData.email}
                    onChange={handleInputChange}
                    placeholder="contato@clinica.com"
                  />
                </div>
              </div>

              <div style={{ display: 'flex', gap: '1rem', justifyContent: 'flex-end' }}>
                <button 
                  type="button"
                  className="btn btn-secondary"
                  onClick={resetForm}
                >
                  Cancelar
                </button>
                <button 
                  type="submit"
                  className="btn btn-primary"
                >
                  {editingClinica ? '💾 Atualizar Clínica' : '💾 Cadastrar Clínica'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Clinicas;
