import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import './App.css';

// Contexts
import { AuthProvider, useAuth } from './contexts/AuthContext';

// Importar componentes
import Dashboard from './components/Dashboard';
import Pacientes from './components/Pacientes';
import Clinicas from './components/Clinicas';
import Consultores from './components/Consultores';
import Agendamentos from './components/Agendamentos';
import Fechamentos from './components/Fechamentos';
import Login from './components/Login';

// √çcones (usando emojis para simplicidade)
const NavItem = ({ to, icon, label, isActive }) => (
  <Link to={to} className={`nav-item ${isActive ? 'active' : ''}`}>
    <span className="nav-icon">{icon}</span>
    <span className="nav-label">{label}</span>
  </Link>
);

const Navigation = () => {
  const location = useLocation();
  const { user, logout, isAdmin, isConsultor } = useAuth();

  // Navega√ß√£o para Admin (acesso total)
  const adminNavItems = [
    { to: '/', icon: 'üìä', label: 'Dashboard' },
    { to: '/pacientes', icon: 'üë•', label: 'Pacientes' },
    { to: '/agendamentos', icon: 'üìÖ', label: 'Agendamentos' },
    { to: '/fechamentos', icon: 'üí∞', label: 'Fechamentos' },
    { to: '/consultores', icon: 'ü©∫', label: 'Consultores' },
    { to: '/clinicas', icon: 'üè•', label: 'Cl√≠nicas' },
  ];

  // Navega√ß√£o para Consultor (acesso limitado)
  const consultorNavItems = [
    { to: '/', icon: 'üìä', label: 'Meu Dashboard' },
    { to: '/pacientes', icon: 'üë•', label: 'Meus Pacientes' },
    { to: '/agendamentos', icon: 'üìÖ', label: 'Meus Agendamentos' },
    { to: '/fechamentos', icon: 'üí∞', label: 'Meus Fechamentos' },
  ];

  const navItems = isAdmin ? adminNavItems : consultorNavItems;

  const handleLogout = () => {
    if (window.confirm('Tem certeza que deseja sair?')) {
      logout();
    }
  };

  return (
    <nav className="sidebar">
      <div className="sidebar-header">
        <h2>ü©∫ CRM Sa√∫de</h2>
        <div className="user-info">
          <div className="user-avatar">
            {isAdmin ? 'üëë' : 'ü©∫'}
          </div>
          <div className="user-details">
            <span className="user-name">{user?.nome}</span>
            <span className="user-role">
              {isAdmin ? 'Administrador' : 'Consultor'}
            </span>
          </div>
        </div>
      </div>
      <div className="nav-items">
        {navItems.map(item => (
          <NavItem
            key={item.to}
            to={item.to}
            icon={item.icon}
            label={item.label}
            isActive={location.pathname === item.to}
          />
        ))}
        <button className="logout-button" onClick={handleLogout}>
          <span className="nav-icon">üö™</span>
          <span className="nav-label">Sair</span>
        </button>
      </div>
    </nav>
  );
};

// Componente de Loading
const LoadingScreen = () => (
  <div className="loading-screen">
    <div className="loading-content">
      <div className="loading-spinner"></div>
      <h2>ü©∫ CRM Sa√∫de</h2>
      <p>Carregando sistema...</p>
    </div>
  </div>
);

// Componente principal protegido
const AuthenticatedApp = () => {
  const { isAdmin } = useAuth();

  // TEMPOR√ÅRIO: Simular usu√°rio admin
  const isAdminTemp = true;

  return (
    <div className="app">
      <Navigation />
      <main className="main-content">
        <Routes>
          <Route path="/" element={<Dashboard />} />
          <Route path="/pacientes" element={<Pacientes />} />
          <Route path="/agendamentos" element={<Agendamentos />} />
          <Route path="/fechamentos" element={<Fechamentos />} />

          {/* Rotas apenas para Admin */}
          {isAdminTemp && (
            <>
              <Route path="/consultores" element={<Consultores />} />
              <Route path="/clinicas" element={<Clinicas />} />
            </>
          )}

          {/* Redirecionar rotas n√£o permitidas para home */}
          <Route path="*" element={<Dashboard />} />
        </Routes>
      </main>
    </div>
  );
};

// Componente principal
const AppContent = () => {
  const { isAuthenticated, loading } = useAuth();

  if (loading) {
    return <LoadingScreen />;
  }

  // TEMPOR√ÅRIO: Sempre mostrar o sistema (sem login)
  return <AuthenticatedApp />;

  // ORIGINAL: return isAuthenticated ? <AuthenticatedApp /> : <Login />;
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <AppContent />
      </Router>
    </AuthProvider>
  );
}

export default App;
