import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Pacientes = () => {
  const [pacientes, setPacientes] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [formData, setFormData] = useState({
    nome: '',
    telefone: '',
    email: '',
    whatsapp: '',
    observacoes: ''
  });

  useEffect(() => {
    fetchPacientes();
  }, []);

  const fetchPacientes = async () => {
    try {
      const response = await axios.get('/api/pacientes');
      setPacientes(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Erro ao carregar pacientes:', error);
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/pacientes', formData);
      setMessage('Paciente cadastrado com sucesso!');
      setShowModal(false);
      setFormData({
        nome: '',
        telefone: '',
        email: '',
        whatsapp: '',
        observacoes: ''
      });
      fetchPacientes();
      
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      console.error('Erro ao cadastrar paciente:', error);
      setMessage('Erro ao cadastrar paciente');
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const formatarData = (data) => {
    return new Date(data).toLocaleDateString('pt-BR');
  };

  const formatarTelefone = (telefone) => {
    if (!telefone) return '';
    // Remove caracteres n√£o num√©ricos
    const numbers = telefone.replace(/\D/g, '');
    // Aplica m√°scara (xx) xxxxx-xxxx
    if (numbers.length === 11) {
      return `(${numbers.substring(0, 2)}) ${numbers.substring(2, 7)}-${numbers.substring(7)}`;
    }
    return telefone;
  };

  return (
    <div>
      <div className="page-header">
        <h1 className="page-title">üë• Gest√£o de Pacientes</h1>
        <p className="page-subtitle">Cadastre e acompanhe seus leads do WhatsApp</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('sucesso') ? 'alert-success' : 'alert-error'}`}>
          {message}
        </div>
      )}

      <div className="card">
        <div className="card-header">
          <h2 className="card-title">üìã Lista de Pacientes</h2>
          <button 
            className="btn btn-primary"
            onClick={() => setShowModal(true)}
          >
            ‚ûï Novo Paciente
          </button>
        </div>

        {loading ? (
          <p>Carregando pacientes...</p>
        ) : pacientes.length === 0 ? (
          <p style={{ textAlign: 'center', color: '#718096', padding: '2rem' }}>
            Nenhum paciente cadastrado ainda. Clique em "Novo Paciente" para come√ßar.
          </p>
        ) : (
          <div className="table-container">
            <table className="table">
              <thead>
                <tr>
                  <th>Nome</th>
                  <th>Telefone</th>
                  <th>WhatsApp</th>
                  <th>Email</th>
                  <th>Status</th>
                  <th>Cadastrado em</th>
                </tr>
              </thead>
              <tbody>
                {pacientes.map(paciente => (
                  <tr key={paciente.id}>
                    <td>
                      <strong>{paciente.nome}</strong>
                      {paciente.observacoes && (
                        <div style={{ fontSize: '0.85rem', color: '#718096', marginTop: '0.25rem' }}>
                          {paciente.observacoes}
                        </div>
                      )}
                    </td>
                    <td>{formatarTelefone(paciente.telefone)}</td>
                    <td>
                      {paciente.whatsapp && (
                        <a 
                          href={`https://wa.me/${paciente.whatsapp.replace(/\D/g, '')}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="btn btn-success"
                          style={{ padding: '0.5rem', fontSize: '0.85rem' }}
                        >
                          üí¨ WhatsApp
                        </a>
                      )}
                    </td>
                    <td>{paciente.email}</td>
                    <td>
                      <span style={{
                        padding: '0.25rem 0.75rem',
                        borderRadius: '12px',
                        fontSize: '0.85rem',
                        background: paciente.status === 'lead' ? '#fff3cd' : '#d4edda',
                        color: paciente.status === 'lead' ? '#856404' : '#155724'
                      }}>
                        {paciente.status === 'lead' ? 'üîç Lead' : '‚úÖ Paciente'}
                      </span>
                    </td>
                    <td>{formatarData(paciente.created_at)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Modal de Cadastro */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2 className="modal-title">‚ûï Novo Paciente/Lead</h2>
              <button 
                className="close-btn"
                onClick={() => setShowModal(false)}
              >
                ‚úï
              </button>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="form-label">Nome Completo *</label>
                <input
                  type="text"
                  name="nome"
                  className="form-input"
                  value={formData.nome}
                  onChange={handleInputChange}
                  placeholder="Digite o nome do paciente"
                  required
                />
              </div>

              <div className="grid grid-2">
                <div className="form-group">
                  <label className="form-label">Telefone</label>
                  <input
                    type="tel"
                    name="telefone"
                    className="form-input"
                    value={formData.telefone}
                    onChange={handleInputChange}
                    placeholder="(11) 99999-9999"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">WhatsApp</label>
                  <input
                    type="tel"
                    name="whatsapp"
                    className="form-input"
                    value={formData.whatsapp}
                    onChange={handleInputChange}
                    placeholder="5511999999999"
                  />
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Email</label>
                <input
                  type="email"
                  name="email"
                  className="form-input"
                  value={formData.email}
                  onChange={handleInputChange}
                  placeholder="email@exemplo.com"
                />
              </div>

              <div className="form-group">
                <label className="form-label">Observa√ß√µes</label>
                <textarea
                  name="observacoes"
                  className="form-textarea"
                  value={formData.observacoes}
                  onChange={handleInputChange}
                  placeholder="Informa√ß√µes adicionais sobre o paciente..."
                  rows="3"
                />
              </div>

              <div style={{ display: 'flex', gap: '1rem', justifyContent: 'flex-end' }}>
                <button 
                  type="button"
                  className="btn btn-secondary"
