import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';

const Dashboard = () => {
  const { makeRequest, usuario } = useAuth();
  const [stats, setStats] = useState({
    totalPacientes: 0,
    totalAgendamentos: 0,
    totalFechamentos: 0,
    valorTotalFechamentos: 0,
    agendamentosHoje: 0,
    leadsPorStatus: {},
    fechamentosPorMes: [],
    consultoresStats: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const [pacientesRes, agendamentosRes, fechamentosRes] = await Promise.all([
        makeRequest('/pacientes'),
        makeRequest('/agendamentos'),
        makeRequest('/fechamentos')
      ]);

      const pacientes = await pacientesRes.json();
      const agendamentos = await agendamentosRes.json();
      const fechamentos = await fechamentosRes.json();

      const hoje = new Date().toISOString().split('T')[0];
      const agendamentosHoje = agendamentos.filter(a => a.data_agendamento === hoje).length;

      const leadsPorStatus = pacientes.reduce((acc, p) => {
        acc[p.status] = (acc[p.status] || 0) + 1;
        return acc;
      }, {});

      const valorTotal = fechamentos.reduce((sum, f) => sum + parseFloat(f.valor_fechado || 0), 0);

      const consultoresMap = {};
      pacientes.forEach(p => {
        if (p.consultor_nome) {
          if (!consultoresMap[p.consultor_nome]) {
            consultoresMap[p.consultor_nome] = {
              nome: p.consultor_nome,
              totalPacientes: 0,
              totalAgendamentos: 0,
              totalFechamentos: 0,
              valorFechado: 0
            };
          }
          consultoresMap[p.consultor_nome].totalPacientes++;
        }
      });

      agendamentos.forEach(a => {
        if (a.consultor_nome && consultoresMap[a.consultor_nome]) {
          consultoresMap[a.consultor_nome].totalAgendamentos++;
        }
      });

      fechamentos.forEach(f => {
        if (f.consultor_nome && consultoresMap[f.consultor_nome]) {
          consultoresMap[f.consultor_nome].totalFechamentos++;
          consultoresMap[f.consultor_nome].valorFechado += parseFloat(f.valor_fechado || 0);
        }
      });

      const consultoresStats = Object.values(consultoresMap);

      setStats({
        totalPacientes: pacientes.length,
        totalAgendamentos: agendamentos.length,
        totalFechamentos: fechamentos.length,
        valorTotalFechamentos: valorTotal,
        agendamentosHoje,
        leadsPorStatus,
        consultoresStats
      });
      setLoading(false);
    } catch (error) {
      console.error('Erro ao carregar estatÃ­sticas:', error);
      setLoading(false);
    }
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(value);
  };

  const getStatusColor = (status) => {
    const colors = {
      lead: '#f59e0b',
      agendado: '#3b82f6',
      compareceu: '#10b981',
      fechado: '#059669',
      nao_fechou: '#ef4444',
      nao_compareceu: '#f87171',
      reagendado: '#8b5cf6'
    };
    return colors[status] || '#6b7280';
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div>
      <div className="page-header">
        <h1 className="page-title">Dashboard</h1>
        <p className="page-subtitle">
          Bem-vindo, {usuario?.nome}
        </p>
      </div>

      {/* KPIs Principais */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-label">Total de Pacientes</div>
          <div className="stat-value">{stats.totalPacientes}</div>
          <div className="stat-change positive">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <polyline points="23 6 13.5 15.5 8.5 10.5 1 18"></polyline>
              <polyline points="17 6 23 6 23 12"></polyline>
            </svg>
            +12% este mÃªs
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-label">Agendamentos</div>
          <div className="stat-value">{stats.totalAgendamentos}</div>
          <div className="stat-change positive">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <polyline points="23 6 13.5 15.5 8.5 10.5 1 18"></polyline>
              <polyline points="17 6 23 6 23 12"></polyline>
            </svg>
            {stats.agendamentosHoje} hoje
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-label">Fechamentos</div>
          <div className="stat-value">{stats.totalFechamentos}</div>
          <div className="stat-change positive">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <polyline points="23 6 13.5 15.5 8.5 10.5 1 18"></polyline>
              <polyline points="17 6 23 6 23 12"></polyline>
            </svg>
            +8% este mÃªs
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-label">Valor Total</div>
          <div className="stat-value">{formatCurrency(stats.valorTotalFechamentos)}</div>
          <div className="stat-change positive">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <polyline points="23 6 13.5 15.5 8.5 10.5 1 18"></polyline>
              <polyline points="17 6 23 6 23 12"></polyline>
            </svg>
            +15% este mÃªs
          </div>
        </div>
      </div>

      <div className="grid grid-2" style={{ gap: '2rem' }}>
        {/* Pipeline de Vendas */}
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">Pipeline de Vendas</h2>
          </div>
          <div className="card-body">
            <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
              {Object.entries(stats.leadsPorStatus).map(([status, count]) => {
                const total = stats.totalPacientes || 1;
                const percentage = (count / total) * 100;
                return (
                  <div key={status}>
                    <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                      <span style={{ fontSize: '0.875rem', fontWeight: '500', textTransform: 'capitalize' }}>
                        {status.replace(/_/g, ' ')}
                      </span>
                      <span style={{ fontSize: '0.875rem', color: '#6b7280' }}>
                        {count} ({percentage.toFixed(1)}%)
                      </span>
                    </div>
                    <div style={{ 
                      width: '100%', 
                      height: '8px', 
                      backgroundColor: '#e5e7eb',
                      borderRadius: '4px',
                      overflow: 'hidden'
                    }}>
                      <div style={{
                        width: `${percentage}%`,
                        height: '100%',
                        backgroundColor: getStatusColor(status),
                        transition: 'width 0.5s ease'
                      }} />
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>

        {/* Performance dos Consultores */}
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">Performance dos Consultores</h2>
          </div>
          <div className="card-body">
            <div className="table-container">
              <table className="table">
                <thead>
                  <tr>
                    <th>Consultor</th>
                    <th style={{ textAlign: 'center' }}>Pacientes</th>
                    <th style={{ textAlign: 'center' }}>Agendamentos</th>
                    <th style={{ textAlign: 'center' }}>Fechamentos</th>
                    <th style={{ textAlign: 'right' }}>Valor</th>
                  </tr>
                </thead>
                <tbody>
                  {stats.consultoresStats.length > 0 ? (
                    stats.consultoresStats.map((consultor, index) => (
                      <tr key={index}>
                        <td style={{ fontWeight: '500' }}>{consultor.nome}</td>
                        <td style={{ textAlign: 'center' }}>{consultor.totalPacientes}</td>
                        <td style={{ textAlign: 'center' }}>{consultor.totalAgendamentos}</td>
                        <td style={{ textAlign: 'center' }}>{consultor.totalFechamentos}</td>
                        <td style={{ textAlign: 'right', fontWeight: '600' }}>
                          {formatCurrency(consultor.valorFechado)}
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" style={{ textAlign: 'center', color: '#9ca3af' }}>
                        Nenhum dado disponÃ­vel
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>

      {/* GrÃ¡fico de ConversÃ£o */}
      <div className="card" style={{ marginTop: '2rem' }}>
        <div className="card-header">
          <h2 className="card-title">Taxa de ConversÃ£o do Funil</h2>
        </div>
        <div className="card-body">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', gap: '2rem' }}>
            <div style={{ flex: 1, textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', fontWeight: '700', color: '#1a1d23' }}>
                {stats.totalPacientes}
              </div>
              <div style={{ fontSize: '0.875rem', color: '#6b7280', marginTop: '0.25rem' }}>
                Leads Totais
              </div>
            </div>

            <svg width="40" height="40" viewBox="0 0 24 24" fill="none" stroke="#d1d5db" strokeWidth="2">
              <polyline points="9 18 15 12 9 6"></polyline>
            </svg>

            <div style={{ flex: 1, textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', fontWeight: '700', color: '#1a1d23' }}>
                {stats.leadsPorStatus.agendado || 0}
              </div>
              <div style={{ fontSize: '0.875rem', color: '#6b7280', marginTop: '0.25rem' }}>
                Agendados
              </div>
            </div>

            <svg width="40" height="40" viewBox="0 0 24 24" fill="none" stroke="#d1d5db" strokeWidth="2">
              <polyline points="9 18 15 12 9 6"></polyline>
            </svg>

            <div style={{ flex: 1, textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', fontWeight: '700', color: '#1a1d23' }}>
                {stats.leadsPorStatus.compareceu || 0}
              </div>
              <div style={{ fontSize: '0.875rem', color: '#6b7280', marginTop: '0.25rem' }}>
                Compareceram
              </div>
            </div>

            <svg width="40" height="40" viewBox="0 0 24 24" fill="none" stroke="#d1d5db" strokeWidth="2">
              <polyline points="9 18 15 12 9 6"></polyline>
            </svg>

            <div style={{ flex: 1, textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', fontWeight: '700', color: '#059669' }}>
                {stats.leadsPorStatus.fechado || 0}
              </div>
              <div style={{ fontSize: '0.875rem', color: '#6b7280', marginTop: '0.25rem' }}>
                Fechados
              </div>
            </div>
          </div>

          <div style={{ 
            marginTop: '2rem', 
            padding: '1rem', 
            backgroundColor: '#f9fafb',
            borderRadius: '8px',
            textAlign: 'center' 
          }}>
            <div style={{ fontSize: '1.5rem', fontWeight: '700', color: '#059669' }}>
              {stats.totalPacientes > 0 
                ? ((stats.leadsPorStatus.fechado || 0) / stats.totalPacientes * 100).toFixed(1)
                : 0}%
            </div>
            <div style={{ fontSize: '0.875rem', color: '#6b7280', marginTop: '0.25rem' }}>
              Taxa de ConversÃ£o Total
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
