import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Clinicas = () => {
  const [clinicas, setClinicas] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingClinica, setEditingClinica] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [formData, setFormData] = useState({
    nome: '',
    endereco: '',
    telefone: '',
    email: ''
  });

  useEffect(() => {
    fetchClinicas();
  }, []);

  const fetchClinicas = async () => {
    try {
      const response = await axios.get('/api/clinicas');
      setClinicas(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Erro ao carregar clínicas:', error);
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingClinica) {
        await axios.put(`/api/clinicas/${editingClinica.id}`, formData);
        setMessage('Clínica atualizada com sucesso!');
      } else {
        await axios.post('/api/clinicas', formData);
        setMessage('Clínica cadastrada com sucesso!');
      }
      
      setShowModal(false);
      setEditingClinica(null);
      setFormData({
        nome: '',
        endereco: '',
        telefone: '',
        email: ''
      });
      fetchClinicas();
      
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      console.error('Erro ao salvar clínica:', error);
      setMessage('Erro ao salvar clínica');
    }
  };

  const handleEdit = (clinica) => {
    setEditingClinica(clinica);
    setFormData({
      nome: clinica.nome || '',
      endereco: clinica.endereco || '',
      telefone: clinica.telefone || '',
      email: clinica.email || ''
    });
    setShowModal(true);
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const formatarData = (data) => {
    return new Date(data).toLocaleDateString('pt-BR');
  };

  const formatarTelefone = (telefone) => {
    if (!telefone) return '';
    const numbers = telefone.replace(/\D/g, '');
    if (numbers.length === 11) {
      return `(${numbers.substring(0, 2)}) ${numbers.substring(2, 7)}-${numbers.substring(7)}`;
    }
    return telefone;
  };

  const resetForm = () => {
    setFormData({
      nome: '',
      endereco: '',
      telefone: '',
      email: ''
    });
    setEditingClinica(null);
    setShowModal(false);
  };

  return (
    <div>
      <div className="page-header">
        <h1 className="page-title">🏥 Gestão de Clínicas</h1>
        <p className="page-subtitle">Cadastre as clínicas parceiras do sistema</p>
      </div>

      {message && (
        <div className={`alert ${message.includes('sucesso') ? 'alert-success' : 'alert-error'}`}>
          {message}
        </div>
      )}

      <div className="card">
        <div className="card-header">
          <h2 className="card-title">🏥 Lista de Clínicas</h2>
          <button 
            className="btn btn-primary"
            onClick={() => setShowModal(true)}
          >
            ➕ Nova Clínica
          </button>
        </div>

        {loading ? (
          <p>Carregando clínicas...</p>
        ) : clinicas.length === 0 ? (
          <p style={{ textAlign: 'center', color: '#718096', padding: '2rem' }}>
            Nenhuma clínica cadastrada ainda. Clique em "Nova Clínica" para começar.
          </p>
        ) : (
          <div className="grid grid-2">
            {clinicas.map(clinica => (
              <div key={clinica.id} className="card" style={{ margin: 0, position: 'relative' }}>
                <div style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  marginBottom: '1rem',
                  paddingBottom: '0.75rem',
                  borderBottom: '2px solid #667eea'
                }}>
                  <span style={{ fontSize: '2rem', marginRight: '0.75rem' }}>🏥</span>
                  <h3 style={{ 
                    fontSize: '1.25rem', 
                    fontWeight: '600', 
                    color: '#2d3748',
                    margin: 0,
                    flex: 1
                  }}>
                    {clinica.nome}
                  </h3>
                  <button
                    onClick={() => handleEdit(clinica)}
                    className="btn btn-secondary"
                    style={{ 
                      padding: '0.5rem', 
                      fontSize: '0.85rem',
                      minWidth: 'auto'
                    }}
                  >
                    ✏️
                  </button>
                </div>
                
                <div style={{ display: 'grid', gap: '0.75rem' }}>
                  {clinica.endereco && (
                    <div style={{ display: 'flex', alignItems: 'flex-start' }}>
                      <span style={{ marginRight: '0.5rem' }}>📍</span>
                      <span style={{ color: '#4a5568' }}>{clinica.endereco}</span>
                    </div>
                  )}
                  
                  {clinica.telefone && (
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                      <span style={{ marginRight: '0.5rem' }}>📞</span>
                      <span style={{ color: '#4a5568' }}>{formatarTelefone(clinica.telefone)}</span>
                    </div>
                  )}
                  
                  {clinica.email && (
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                      <span style={{ marginRight: '0.5rem' }}>📧</span>
                      <a 
                        href={`mailto:${clinica.email}`}
                        style={{ color: '#667eea', textDecoration: 'none' }}
                      >
                        {clinica.email}
                      </a>
                    </div>
                  )}
                  
                  <div style={{ 
                    marginTop: '1rem',
                    paddingTop: '0.75rem',
                    borderTop: '1px solid #e2e8f0',
                    fontSize: '0.85rem',
                    color: '#718096'
                  }}>
                    Cadastrada em: {formatarData(clinica.created_at)}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Modal de Cadastro/Edição */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2 className="modal-title">
                {editingClinica ? '✏️ Editar Clínica' : '🏥 Nova Clínica'}
              </h2>
              <button 
                className="close-btn"
                onClick={resetForm}
              >
                ✕
              </button>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="form-label">Nome da Clínica *</label>
                <input
                  type="text"
                  name="nome"
                  className="form-input"
                  value={formData.nome}
                  onChange={handleInputChange}
                  placeholder="Digite o nome da clínica"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Endereço Completo</label>
                <input
                  type="text"
                  name="endereco"
                  className="form-input"
                  value={formData.endereco}
                  onChange={handleInputChange}
                  placeholder="Rua, número, bairro, cidade - CEP"
                />
              </div>

              <div className="grid grid-2">
                <div className="form-group">
                  <label className="form-label">Telefone</label>
                  <input
                    type="tel"
                    name="telefone"
                    className="form-input"
                    value={formData.telefone}
                    onChange={handleInputChange}
                    placeholder="(11) 99999-9999"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    name="email"
                    className="form-input"
                    value={formData.email}
                    onChange={handleInputChange}
                    placeholder="contato@clinica.com"
                  />
                </div>
              </div>

              <div style={{ display: 'flex', gap: '1rem', justifyContent: 'flex-end' }}>
                <button 
                  type="button"
                  className="btn btn-secondary"
                  onClick={resetForm}
                >
                  Cancelar
                </button>
                <button 
                  type="submit"
                  className="btn btn-primary"
                >
                  {editingClinica ? '💾 Atualizar Clínica' : '💾 Cadastrar Clínica'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Clinicas;
