import React, { useState } from 'react';
import './App.css';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import LandingPage from './components/LandingPage';
import CadastroConsultor from './components/CadastroConsultor';
import CadastroSucesso from './components/CadastroSucesso';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import Pacientes from './components/Pacientes';
import Consultores from './components/Consultores';
import Clinicas from './components/Clinicas';
import Agendamentos from './components/Agendamentos';
import Fechamentos from './components/Fechamentos';

function AppContent() {
  const { user, logout, loading } = useAuth();
  const [activeTab, setActiveTab] = useState('dashboard');
  const [currentView, setCurrentView] = useState('landing'); // landing, cadastro, cadastro-sucesso, login

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  // Se o usuário não está autenticado, mostrar as páginas de entrada
  if (!user) {
    if (currentView === 'landing') {
      return (
        <LandingPage 
          onCadastro={() => setCurrentView('cadastro')}
          onLogin={() => setCurrentView('login')}
        />
      );
    }

    if (currentView === 'cadastro') {
      return (
        <CadastroConsultor 
          onVoltar={() => setCurrentView('landing')}
          onCadastroSucesso={() => setCurrentView('cadastro-sucesso')}
        />
      );
    }

    if (currentView === 'cadastro-sucesso') {
      return (
        <CadastroSucesso 
          onIrParaLogin={() => setCurrentView('login')}
        />
      );
    }

    if (currentView === 'login') {
      return <Login onVoltar={() => setCurrentView('landing')} />;
    }
  }

  // Se o usuário está autenticado, mostrar a aplicação principal
  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return <Dashboard />;
      case 'pacientes':
        return <Pacientes />;
      case 'consultores':
        return <Consultores />;
      case 'clinicas':
        return <Clinicas />;
      case 'agendamentos':
        return <Agendamentos />;
      case 'fechamentos':
        return <Fechamentos />;
      default:
        return <Dashboard />;
    }
  };

  const getUserInitials = () => {
    if (user.nome) {
      const names = user.nome.split(' ');
      if (names.length >= 2) {
        return names[0][0] + names[names.length - 1][0];
      }
      return names[0][0] + names[0][1];
    }
    return 'U';
  };

  return (
    <div className="App">
      <aside className="sidebar">
        <div className="sidebar-header">
          <h1 className="sidebar-title">CRM System</h1>
          <p className="sidebar-subtitle">Enterprise Edition</p>
        </div>

        <nav className="sidebar-nav">
          <div className="nav-item">
            <a
              href="#dashboard"
              className={`nav-link ${activeTab === 'dashboard' ? 'active' : ''}`}
              onClick={(e) => {
                e.preventDefault();
                setActiveTab('dashboard');
              }}
            >
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <rect x="3" y="3" width="7" height="7" />
                <rect x="14" y="3" width="7" height="7" />
                <rect x="14" y="14" width="7" height="7" />
                <rect x="3" y="14" width="7" height="7" />
              </svg>
              Dashboard
            </a>
          </div>

          <div className="nav-item">
            <a
              href="#pacientes"
              className={`nav-link ${activeTab === 'pacientes' ? 'active' : ''}`}
              onClick={(e) => {
                e.preventDefault();
                setActiveTab('pacientes');
              }}
            >
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2" />
                <circle cx="9" cy="7" r="4" />
                <path d="M23 21v-2a4 4 0 0 0-3-3.87" />
                <path d="M16 3.13a4 4 0 0 1 0 7.75" />
              </svg>
              Pacientes
            </a>
          </div>

          <div className="nav-item">
            <a
              href="#agendamentos"
              className={`nav-link ${activeTab === 'agendamentos' ? 'active' : ''}`}
              onClick={(e) => {
                e.preventDefault();
                setActiveTab('agendamentos');
              }}
            >
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <rect x="3" y="4" width="18" height="18" rx="2" ry="2" />
                <line x1="16" y1="2" x2="16" y2="6" />
                <line x1="8" y1="2" x2="8" y2="6" />
                <line x1="3" y1="10" x2="21" y2="10" />
              </svg>
              Agendamentos
            </a>
          </div>

          <div className="nav-item">
            <a
              href="#fechamentos"
              className={`nav-link ${activeTab === 'fechamentos' ? 'active' : ''}`}
              onClick={(e) => {
                e.preventDefault();
                setActiveTab('fechamentos');
              }}
            >
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <line x1="12" y1="1" x2="12" y2="23" />
                <path d="M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6" />
              </svg>
              Fechamentos
            </a>
          </div>

          {user.tipo === 'admin' && (
            <>
              <div className="nav-item">
                <a
                  href="#consultores"
                  className={`nav-link ${activeTab === 'consultores' ? 'active' : ''}`}
                  onClick={(e) => {
                    e.preventDefault();
                    setActiveTab('consultores');
                  }}
                >
                  <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2" />
                    <circle cx="12" cy="7" r="4" />
                  </svg>
                  Consultores
                </a>
              </div>

              <div className="nav-item">
                <a
                  href="#clinicas"
                  className={`nav-link ${activeTab === 'clinicas' ? 'active' : ''}`}
                  onClick={(e) => {
                    e.preventDefault();
                    setActiveTab('clinicas');
                  }}
                >
                  <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z" />
                    <polyline points="9 22 9 12 15 12 15 22" />
                  </svg>
                  Clínicas
                </a>
              </div>
            </>
          )}
        </nav>

        <div style={{ marginTop: 'auto', padding: '1rem', borderTop: '1px solid rgba(255, 255, 255, 0.1)' }}>
          <div className="user-info">
            <div className="user-avatar">
              {getUserInitials()}
            </div>
            <div className="user-details">
              <h3>{user.nome}</h3>
              <p>{user.tipo === 'admin' ? 'Administrador' : 'Consultor'}</p>
            </div>
          </div>
          <button
            onClick={logout}
            className="nav-link"
            style={{ marginTop: '1rem', color: '#ef4444' }}
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4" />
              <polyline points="16 17 21 12 16 7" />
              <line x1="21" y1="12" x2="9" y2="12" />
            </svg>
            Sair
          </button>
        </div>
      </aside>

      <main className="main-content">
        <header className="main-header">
          <div>
            <h2 style={{ fontSize: '1.5rem', fontWeight: '600', color: '#111827', margin: 0 }}>
              {activeTab === 'dashboard' && 'Dashboard'}
              {activeTab === 'pacientes' && 'Gestão de Pacientes'}
              {activeTab === 'consultores' && 'Gerenciar Consultores'}
              {activeTab === 'clinicas' && 'Gerenciar Clínicas'}
              {activeTab === 'agendamentos' && 'Agendamentos'}
              {activeTab === 'fechamentos' && 'Fechamentos'}
            </h2>
          </div>
          <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>
            {new Date().toLocaleDateString('pt-BR', { 
              weekday: 'long', 
              year: 'numeric', 
              month: 'long', 
              day: 'numeric' 
            })}
          </div>
        </header>

        <div className="page-content">
          {renderContent()}
        </div>
      </main>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
}

export default App;
